mapper.select:
select id,sku,country,urgencyCode,createByAuto,artImagePath,threeDPlatform, Parent_UrgencyCode,laterStagePath,renderPath,checkState from cxtrade.dbo.PM2_Art_Works
((COALESCE(CheckState, 0) != 10 and urgencyCode != 90 )or (urgencyCode = 90 and CheckState = 1)) and ArtImagePath IS NOT NULL and ArtImagePath != ''
and sku like concat('%', #{cpbh}, '%')
and sku in
#{cpbh}
and (urgencyCode = 90 or country IN
#{item}
)

select id,sku,country,urgencyCode,createByAuto,artImagePath,threeDPlatform, Parent_UrgencyCode,laterStagePath,renderPath,checkState from cxtrade.dbo.PM2_Art_Works
COALESCE(CheckState, 0) != 10 and ArtImagePath IS NOT NULL and ArtImagePath != ''
and sku =#{cpbh}

SELECT Id, country, sku as cpbh, Languaeg as language , Title, BulletPoint, Description ,Feature, Specification,SpecialOption From cxtrade.dbo.PM2_Sku_Content_Records
and sku like concat('%', #{cpbh}, '%')
and sku in
#{cpbh}
and country IN
#{item}
and Languaeg IN
#{item}
AND ( SpecialOption IS NULL OR SpecialOption != 'Temu专属文案' OR (SpecialOption = 'Temu专属文案' AND Status = 4))

SELECT a.Id AS WAID, b.Id, a.SKU, a.Country, b.Title, b.BulletPoint, b.Description, b.Feature, b.Specification, b.Languaeg FROM cxtrade.dbo.PM_SKU_Distribution a INNER JOIN cxtrade.dbo.PM2_Sku_Content_Records b ON a.SKU = b.Sku AND a.Country = b.Country AND a.RelationGuid = b.RelationGuid AND a.TaskMemo = b.TaskMemo WHERE a.Id IN ( SELECT WAID FROM cxtrade.dbo.PM2_Art_Works WHERE ISNULL(CheckState, 0) != 10 AND ArtImagePath IS NOT NULL AND ArtImagePath != '' AND NodeId = '2999' AND WAID IS NOT NULL AND WAID != '' and sku like concat('%', #{cpbh}, '%')
and sku in
#{cpbh}
and country IN
#{item}
)

mapper.update:
update cxtrade.dbo.pim_pm_minio_image_path
set is_disable = #{isDisable}
where id = #{id}
